import React, { useState } from 'react'


const AddUserForm = props => {
	const initialFormState = { id: null, a: '', b: '', c: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
  }

  const delta = () => {
    //var delta = user.a + user.b
    var insere = document.getElementById('a').value

    insere.innerHTML = "yes"
  }

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
        if (!user.a || !user.b || !user.c) return

        if(isNaN(user.a)){
					var erreur = document.getElementById('erreur')
          erreur.innerHTML = 'Entrer un nombre'
        }
        else if(isNaN(user.b)){
          var erreur1 = document.getElementById('erreur1')
          erreur1.innerHTML = 'Entrer un nombre'
        }
        else if(isNaN(user.c)){
          var erreur2 = document.getElementById('erreur2')
          erreur2.innerHTML = 'Entrer un nombre'
        }
      else{
					var yes = document.getElementById('erreur')
          yes.innerHTML = "";
          var err1 = document.getElementById('erreur1')
          err1.innerHTML = "";
          var err2 = document.getElementById('erreur2')
          err2.innerHTML = "";
					props.addUser(user)
          setUser(initialFormState)	

			}
			}}
		>		<center className="tableau">

          <table>
            <tbody>
              <tr>
                <td><label id="nom">Indice a &nbsp;&nbsp;</label></td>
                <td><input type="text" name="a" value={user.a} onChange={handleInputChange} />
                <p id="erreur1"></p>
                </td>
              </tr><br/>
              <tr>
                <td><label id="nom">Indice b</label></td>
                <td><input id="inputprix" type="text" name="b" value={user.b} onChange={handleInputChange} />
                <p id="erreur"></p>
                </td>
              </tr>
              <tr>
                <td><label id="nom">indice c</label></td>
                <td><input type="text"  name="c" value={user.c} onChange={handleInputChange} />
                <p id="erreur2"></p>
                <p id="a"></p>
                </td>
              </tr>
            </tbody>
            </table>
            <button id="boutton" onClick={() => {delta()}} className="btn btn-primary btn-lg">Calculer</button>

    </center>
		</form>

	)
}	
export default AddUserForm;
