import React, { useState } from 'react'
import './App.css';


const Commentaire = () => {

  const Data = []

  const [use, setUse] = useState(Data)

  const commentajout = user => {
		user.id = use.length + 1
		setUse([ ...use, user ])
  }

	const initialFormState = { id: null, commentaire: ''}
	const [ usere, setUserse ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUserse({ ...usere, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
                if (!usere.commentaire ) return

          var erreurvide = document.getElementById('vide')
          if(erreurvide.value === ""){
            var vide = document.getElementById('para')
            vide.innerHTML = "Veuillez remplir le champ"
          }else{
            var yes = document.getElementById('para')
            yes.innerHTML = ""
            commentajout(usere)
            setUserse(initialFormState)	
          }
          //console.log(use.length)

          for(var i=0;i<use.length;i++){
            console.log(use[i])
          }
          var tab = [];
          var push = tab.map(use[i])
          //console.log(push)
			}}
		>	

          <table id="tablecommentaire">
            <tbody>
              <tr>
                <td><input type="text" id="vide" name="commentaire" value={usere.commentaire} onChange={handleInputChange} />
                <p id="para"></p>
                &nbsp;<button className="btn btn-primary btn-lg">Comment</button>
                </td>
              </tr><br/>
            </tbody>
            </table>

              <table><hr id="ligne"/>
                {use.length > 0 ? 
                (
                    use.map(usere => (
                      <tbody>
                    <tr key={usere.id}>
                    <td id="tdcomment"><div id="word">{usere.commentaire}</div></td>
                  </tr><hr id="ligne"/>
                    </tbody>  
              ))
            ) : (
              <tr>
                <td></td>
              </tr>
            )}
                </table>



		</form>

	)
}
export default Commentaire;
